/// <summary>
/// Клиент API для биржи Binance
/// </summary>
public class RMarketApiClientBinance extends RMarketApiClient
{

}

Method.Source:
protected int64 getServerTimeMS()
{
    RMarketMethod_Binance_Time apiMethod = new RMarketMethod_Binance_Time();
    apiMethod.setBaseUrl(this.parmBaseUrl());
    apiMethod.execute();
    int64 miliSecond = apiMethod.miliSecond();

    return miliSecond;
}

/// <summary>
/// Построение аутентификации для Binance
/// </summary>
protected void buildAuth()
{
    if (!apiKey || !apiKey.ApiKey || !apiKey.Secret) return;

    // Для Binance требуется подпись и временная метка
    int64 timestamp = this.getServerTimeMS();
    this.addQueryParam('timestamp', int642Str(timestamp));

    // Генерация подписи
    //str signature = this.generateSignature();
    //this.addQueryParam('signature', signature);

    // Добавление API-ключа
    this.addHeader('X-MBX-APIKEY', apiKey.ApiKey);
}

protected void buildSignature()
{
    if (!apiKey || !apiKey.ApiKey || !apiKey.Secret) return;

    // Генерация подписи
    str signature = this.signHMACSHA256(methodQuery, apiKey.Secret);
    this.addQueryParam('signature', signature);

    this.buildMethodQuery();    // Шаг 4: перестроение с учетом подписи
}
