/// <param name="_clientOrderId"> Клиентский ID ордера </param>
/// <returns> Результат удаления ордера </returns>
public RMarketJson_Base deleteOrder(
    str _symbol,
    str _orderId = '',
    str _clientOrderId = '')
{
    RMarketApiClientBinance apiClient = this.getApiClient();
    str url = apiClient.parmBaseUrl() + '/api/v3/order';

    Map params = new Map(Types::String, Types::String);
    params.insert('symbol', _symbol);

    if (_clientOrderId)
    {
        params.insert('origClientOrderId', _clientOrderId);
    }
    else
    {
        params.insert('orderId', _orderId);
    }

    str response = apiClient.delete(url, params);
    return apiClient.deserialize(response, classStr(RMarketJson_Binance_DeleteOrder));
}
