/// apiMethod.execute();
/// RMarketTimestamp second = apiMethod.second();
/// int64 miliSecond = apiMethod.miliSecond();
/// </summary>
public class RMarketMethod_Binance_Time extends RMarketMethod implements RMarketMethodInterface_Time
{
    protected RMarketJson_Binance_Time responseContract;

}

Method.Source:
/// <summary> Конструктор класса </summary>
public void new()
{
    apiClient = new RMarketApiClientBinance();
    apiClient.parmHttpMethod(RMarketAPIHttpMethod::GET);
    apiClient.parmMethodUrl('/api/v3/time');

    // Do this before execute()
    //apiClient.parmBaseUrl('');
    //apiClient.parmApiKey();
}

public void prepare()
{
    // Add your code here...

    super();
}

public void processResponse()
{
    super();

    // Ответ в мс: {"serverTime": 1499827319559}
    str responseStr = apiClient.getResponseBody();

    if (apiClient.getHttpStatus() != System.Net.HttpStatusCode::OK)
        throw Error(strfmt("Request returns error!"));

    responseContract = RMarketJsonSerializer::deserializeObject(
        classNum(RMarketJson_Binance_Time),
        responseStr) as RMarketJson_Binance_Time;

}

public RMarketTimestamp second()
{
    return responseContract.parmServerTime() div 1000;
}
