/// <summary> Конструктор класса </summary>
public void new()
{
    apiClient = new RMarketApiClientBybit();
}

/// <summary>
/// Устанавливает параметры запроса
/// </summary>
/// <param name="_accountType">Тип аккаунта</param>
/// <param name="_coin">Криптовалюта (опционально)</param>
public void setParameters(str _accountType = 'UNIFIED', str _coin = '')
{
    accountType = _accountType;
    coin = _coin;
}

public void prepare()
{
    //apiClient.setEndpoint('/v5/asset/transfer/query-account-coins-balance');
    apiClient.addQueryParam('accountType', accountType);

    if (coin)
    {
        apiClient.addQueryParam('coin', coin);
    }

    // Требуется аутентификация
    if (apiClient.parmApiKey())
    {
        //int64 timestamp = DateTimeUtil::getSystemDateTime();
        //apiClient.addHeader('X-BAPI-TIMESTAMP', int642Str(timestamp));
        apiClient.addHeader('X-BAPI-API-KEY', apiClient.parmApiKey().ApiKey);
        apiClient.addHeader('X-BAPI-RECV-WINDOW', '5000');

        // Подпись запроса
        // client.signRequest(timestamp);
    }
}

